INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (2, 'ИП Кочерго  ', '', 'logos/3133_4_1422609972_1893981_1.jpg', 1, 'Мелкий ремонт с гарантией качества и недорого. Подключение бытовой техники, ремонт сантехники, розеток, сборка мебели, навеска карнизов, люстр, ремонт замков, компьютерная помощь и др. Работы по дому, офису, квартире. \r\nБолее подробную информацию и цены смотрите на сайте компании.http://masternadomy.by \r\nЗвоните, пишите всем ответим!!!\r\n\r\n-Доставка материалов \r\n-Возможна оплата по б/налу ', '', '', '', '80296939396', 'http://masternadomy.by/', '80292939595', NULL, NULL, NULL);
