INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (4, 'ООО"ТрейдОМплюс"', 'проспект Октября 67 ,офис 3-5246000	', 'logos/3026_4_1419329391__.jpg', 3, '\r\nПредлагаем услугу - Муж на час - любой мелкий бытовой ремонт в Гомеле, необходимость которого может возникнуть в домашних условиях и выполнять который, по каким-либо причинам Вы сами не хотите или не можете. По услуге муж на час - качество и быстрые сроки выполнения работ гарантированы! Окажем помощь в приобретении материалов и их доставке на дом. \r\n\r\n\r\n\r\nКраткий перечень работ по услуге - Муж на час \r\nМелкий бытовой ремонт на дому - оплата за время работы \r\nУстановка карнизов для штор и пр. \r\nСверление различного рода отверстий в стенах, полу и пр. \r\nНавес зеркал в ванную, коридор и др. места \r\nНавес шкафов и шкафчиков в помещения, ванную, туалет, коридор и др.\r\nИзготовление, замена или ремонт различных видов полок и полочек \r\nРемонт, установка, замена оконной и дверной фурнитуры \r\nРемонт, замена или врезка дверных замков на наружные и внутренние двери \r\nЗамена, вырезка стекол \r\nУстановка, замена, покраска плинтусов в квартирах, домах, офисах \r\nРемонт или замена кафельной плитки или других настенных, потолочных или напольных покрытий \r\nУстановка настенных, напольных креплений под домашние кинотеатры, домашнюю видео-аудио технику \r\nРемонт замков, врезка замков в двери, шкафы \r\nРаботы по ремонту, замене сантехники \r\nРемонт электрики \r\nи другие работы, мужская помощь по дому \r\n\r\n\r\nРасценки на выполнение ремонтных работ по услуге -Муж на час \r\nМы работаем по ценам средним и ниже средних по городу! В течении часа или другого часового или необходимого отрезка времени мастером - мужем на час могут быть выполнены сразу несколько видов ремонта или мелких работ по дому в которых требуется мужская помощь. Вы просто оплачиваете время работы вне зависимости от количества выполненных мужем на час задач.\r\n\r\nВыезд на замер и оценку работы бесплатно \r\nДоговор на выполнение работ \r\nДоставка материалов \r\nВозможна оплата по б/налу ', 'topofice@mail.ru', '', '80232232269', '+37529 129 18 53', '', '+37529 6290159', '', '', NULL);
