INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (8, '"РеМастер" ИП Нестеров А.А.  ', 'ул. Притыцкого 112', 'logos/487_4_1360608236___..jpg', 1, '\r\nВыезд мастера в удобное для Вас время. Квалифицированный подход к решению поставленной клиентом задачи. Фиксированная почасовая оплата услуг мастера. Установка бытовой техники и креплений, сборка мебели, установка карнизов, жалюзи, сушилок, столешниц, врезка встраиваемой техники. Другие ремонтные работы.\r\n\r\nДоставка материалов \r\n\r\nЦена ........... 85000 ', 'nesterov3105@mail.ru', '', '', '+375 29 372 65 16', 'http://www.remaster.by/', '', NULL, NULL, NULL);
INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (9, ' ИП Деткович', '8-029-674-28-05', '', 4, 'ИП Деткович Александр Иванович. \r\n\r\nВыполняем ремонт квартир, офисов, коттеджей и других помещений. \r\nОклейка различными видами обоев, шпатлевание различных поверхностей, окраска, оштукатуривание, отделка откосов, арок, монтаж пробки, потолочного плинтуса и другой широкий спектор услуг.А также различные подготовительные работы. \r\nКачество гарантируем . \r\nВозможность доставки материалов. Консультация по работе. Наличный и безналичный расчёт. \r\n\r\n\r\nИП Деткович \r\nУНП 191458178\r\n\r\nВыезд на замер и оценку работы бесплатно \r\nДоговор на выполнение работ \r\nДоставка материалов \r\nВозможна оплата по б/налу \r\n', 'stroika.ale@yandex.by', '', '', '8-029-674-28-05', '', '', NULL, NULL, NULL);
INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (10, 'Dekora  ', '', '', 4, 'РОСПИСЬ СТЕН - оформление стен жилых домов, офисов, кафе, ресторанов, клубов. \r\nОригинальный, необычный, яркий способ привнести ноту необычности в Ваш интерьер, сделать его незабываемым, вдохнуть в него свой ритм. Для каждого проекта разрабатывается уникальный эскиз, исходя из пожеланий заказчика и особенностей его интерьера. ', 'elena_mix_rh@mail.ru', '', '', '+375445655421', '', '+375295161303', NULL, NULL, NULL);
INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (11, 'Студия Декора "Яхорс"  ', '', 'logos/458_4_1360440325_130837541.jpg', 1, 'Фрески созданы на основе экологически чистых материалов и представляют собой полотна, которые с легкостью клеятся на любую основу. Особенностью Фресок является поверхность, полностью имитирующая старинную стену. Изготавливаются они художниками, и 90% работы выполняется вручную,начиная с выполнения эскиза и заканчивая покрытием защитным лаком. Фрески долговечны, не имеют ограничений в размере и не боятся воды.', 'yahors@live.ru', '', '', '+375291780766', 'http://www.beldecor.by/', '+37529-178-07-66', NULL, NULL, NULL);
INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (12, 'ИП Дударев М.М', '', 'logos/70_4_1357582006_Uslugi_jelektrika_Minsk.jpg', 1, 'Навеска картин, карнизов, полочек, рейлингов, зеркал, кронштейнов, корпусной мебели в Минске и пригороде. \r\n\r\nМастера оказывают услугу навеска зеркал, карнизов (в том числе, на эркер) и других предметов интерьера и мебели. Оперативность и качество проведенных работ гарантировано высоким профессионализмом и многолетним опытом мастеров. \r\nМы предлагаем Вам следующий перечень услуг: \r\nНавеска полок \r\nНавеска карнизов \r\nНавеска зеркал \r\nНавеска крючков и рейлингов на кухне \r\nНавеска шкафчиков \r\nУстановка наборов для ванной и туалета \r\nСверление отверстий в бетоне и кафеле и др. \r\nНавеска полок и кронштейнов под ТВ \r\nНавеска полок и кронштейнов под микроволновую печь \r\nУстановка спортивных стенок \r\n\r\nСборка и ремонт корпусной мебели \r\n\r\nСборка мебели представляет собой сложный процесс, мы рекомендуем не затевать самостоятельную сборку, а сразу обратиться к профессионалам. Это позволит сэкономить Вам время и свести на нет возможность собрать мебель некачественно или повредить её во время сборки. \r\n\r\nПорядок и схема сборки Вашей мебели напрямую будет зависеть от её типа, поэтому учесть все тонкости процесса смогут только опытные, квалифицированные специалисты. Кроме того, именно от качества сборки будет зависеть исправная работа и срок службы вашей мебели. \r\n\r\nНаши мастера используют для работы только профессиональное современное оборудование, которое в совокупности с солидным опытом и высоким уровнем профессиональных навыков позволяет быстро и грамотно осуществлять сборку корпусной мебели от любых производителей. \r\n\r\nМы также осуществляем ремонт любой корпусной мебели. Она очень популярна, ее можно встретить практически в каждой квартире, но, как любая другая, корпусная мебель со временем теряет свои качества и требует ремонта. \r\n\r\nНаши мастера подарят Вашей мебели «вторую жизнь». Большой опыт работы с корпусной мебелью от разных производителей, высокая профессиональная грамотность и применение в работе только качественных материалов и современного специализированного оборудования позволяют с уверенностью гарантировать успех мероприятия. Наши специалисты осмотрят изделие и предложат Вам оптимальный вариант ремонта, а также помогут с выбором материалов и фурнитуры. \r\n\r\nМы всегда рады помочь Вам и предложить высококачественные услуги по выгодным ценам! \r\n\r\n\r\nПредоставим свои услуги по ремонту просевших дверей и установке замков для металлических и деревянных дверей. Мы специализируемся на установке замков и ремонте дверей любой сложности. Обратившись к нам, Вы получите весь спектр услуг, связанных с установкой и заменой замков на дверях любого типа. Гарантия качества присутствует в первую очередь. \r\n\r\nЗа все предшествующие годы нашими специалистами накоплен огромный опыт работы по установке нестандартных замков. Воспользовавшись нашими услугами, Вы будете удивлены ценой и качеством выполненной работы. \r\n\r\nПрофессиональные услуги по установке плинтусов и пластиковых откосов. \r\nОконные откосы служат декоративной частью любого современного окна. Кроме того, что они закрывают собой неэстетичные сколы между окном и стеной здания, некоторые типы оконных откосов, так же, эффективно выполняют теплоизоляционные и вентиляционные функции и поэтому очень важно проведенная правильно установка откосов. \r\n\r\n\r\nПодключение стиральных машин к центральной канализации. \r\n\r\nЗвоните нам и заказывайте подключение стиральной машины! Наш мастер оперативно и качественно приедет к Вам на помощь и произведёт установку стиральной машины с соблюдением всех инструкций и требований от завода изготовителя! Рады сотрудничеству готовы помочь в любую минуту! \r\n\r\nМы выполняем подключение стиральной машины по всему Минску и пригороду! Рады придти Вам на помощь и оказать качественные услуги. Стиральные машины давно вошли в нашу жизнь принося нам экономию времени и комфорт! Выполнить качественное подключение машинки, значит соблюсти все мелочи влияющие на дальнейшую её работу! Эти мелочи известны только специалисту! \r\n\r\nЗвоните: 29-706-34-92 / 44-717-10-88 \r\nЗаходите: jelektrik.by\r\n\r\nВыезд на замер и оценку работы бесплатно \r\nДоговор на выполнение работ \r\nДоставка материалов \r\nВозможна оплата по б/налу ', 'jelektro@ya.ru', '', '', '8044 717 10 88', 'http://www.jelektrik.by/', '8029 706 34 92', NULL, NULL, NULL);
INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (13, 'Центр бытовых услуг 124', '', '', 1, '\r\nМелкий бытовой ремонт от «Центра бытовых услуг 124» . \r\n\r\nНебольшой ремонт в квартире или офисе: положим плитку на кухне и в ванной, поклеим обои, положим ламинат, повесим карниз, установим батарею, и выполним любой другой бытовой ремонт. \r\n\r\n\r\nДоговор на выполнение работ \r\nВозможна оплата по б/налу \r\nТелефон: 124 (многоканальный, для всех операторов)', '', '', '124', '124', 'http://124.by/', '', NULL, NULL, NULL);
INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (14, 'ИП Будай И.А.  ', '', 'logos/2268_4_1391504246_DSC_0048.jpg', 4, '\r\nТолько в ФЕВРАЛЕ!!! \r\nАкции и Скидки% \r\n\r\n1 закажи отделку балкона и получи в подарок сушилку потолочную или скидку 5% \r\n2 закажи обшивку санузлов панелями ПВХ и получи в подарок сушилку потолочную или скидку 5% \r\n3 закажи потолки из ПВХ панелей и получи в подарок встраиваемые светильники или скидку10% \r\n4 при заказе потолков на кухне и санузлах, в туалете -БЕСПЛАТНО... \r\n\r\nБалкон - это 4 квадратных метра квартиры. Сколько они сейчас стоят? Так давайте их ценить. Каждый год зима наступает неожиданно и наши балконы заваливаются снегом, на них льет дождь, а можно наши балконы использовать с пользой. Каждый балкон - это место для сушки белья, место отдыха и складирования закаток, овощей и спортивного инвентаря. \r\n\r\nПри помощи обшивки, которая включает в себя отделку стен, потолка, пола, удается скрыть элементы утеплителя и электропроводку. Профессионально выполненная работа исключит проникновение шума и холода в помещение, позволяя достичь отличной шумо- и теплоизоляции. Профессиональная внутренняя отделка подарит стильный, неповторимый, эстетичный внешний вид балкону, создающий особое настроение и для отдыха, и для работы. \r\n\r\n\r\n\r\nБалконы и лоджии: виды отделки \r\n\r\nДля того, чтобы конструкция была максимально прочной и надежной, необходимо провести грамотные подготовительные мероприятия: аккуратный демонтаж со снятием старой отделки, удаление встроенной мебели и др. \r\n\r\nМы, на высоком профессиональном уровне осуществляем работы по реконструкции практически всех балконов и любых лоджий: \r\n\r\nВнутренняя отделка лоджий, включающая обшивку деревянной вагонкой, стеновыми панелями ПВХ или МДФ. \r\n\r\nУтепление при отделке балконов – это немаловажная составляющая будущего комфорта и уюта. Гарантирована полная ветро- и влагоизоляция. \r\n\r\nНастил пола пола на балконе или лоджии \r\n\r\nУстановку сушилки для белья – которая придется по вкусу современным хозяйкам: недорогая, практичная, удобная модель \r\n\r\nМонтаж электропроводки осветительных приборов, розеток, выключателей и других устройств, необходимых при обустройстве балкона \r\n\r\nУстановку встроенных шкафов \r\n\r\n\r\n\r\nА также предлагаем ряд дополнительных услуг, которые помогут улучшить качество вашей жизни: \r\n\r\nОбшивка ванных комнат \r\n\r\nОбшивка туалетов \r\n\r\nПотолки из панелей ПВХ и др.. \r\n\r\nЗанимаясь ремонтом балконов в Минске мы привыкли выполнять свою работу на совесть. Вы можете, не сомневаясь, поручить нам выполнение самых сложных работ - возможно, даже тех, которые не смогли выполнить другие мастера. \r\n\r\nP.S. Ваш только звонок, все остальное делаем Мы...!!! ', '', '', '', '8 033 306-68-14', 'http://D-os.by/', '8 029 128-68-14', NULL, NULL, NULL);
