INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (25, 'ИП Юркевич В.А.  ', ' ул. Плеханова 55', '', 1, '\r\nВыезжаем в кратчайшие сроки, цены гибкие. Стаж электриков до 20 лет. Выполняем работы по электрике любых объёмов и сложности. Расчёт наличный/безналичный в белорусских рублях без привязки к валюте. \r\n\r\nПредлагаем полный комплекс электромонтажных работ в квартирах, коттеджах, офисных зданиях, помещениях нежилого типа (склады, гаражи и др.): \r\n- монтаж систем электроснабжения зданий и сооружений любого назначения промышленного, общественного и жилого; \r\n- устройство трасс и линий для прокладки кабеля (устройство борозд, прокладка лотков, прокладка короба и т.д.); \r\n- прокладка кабеля силового (скрыто, открыто); \r\n- прокладка слаботочных кабелей (компьютерные, телефонные и телевизионные); \r\n- монтаж электрических вводных и распределительных щитов; \r\n- монтаж электроустановочных изделий (розетки, выключатели и т.д.);\r\n- монтаж силового электрооборудования (вентиляторы, кондиционеры и т.д.); \r\n- монтаж электрооборудования освещения (люстры, светильники, прожекторы и т.д.); \r\n\r\nВ комплекс перечня по электрике входят различные виды работ: \r\n- укладка силового кабеля с различным сечением; \r\n- прокладка труб ПВХ по основанию пола, по стенам и потолкам с диаметром условного прохода с затяжкой провода; \r\n- установка стальных и пластиковых коробов по стенам, потолкам и перегородкам; \r\n- резка штроб различного размера по силикату, гипсокартону, штукатурке, кирпиче, бетоне; \r\n- установка подрозетников, распаечных коробок, розеток и выключателей, проходных выключателей; \r\n- коммутация электрических цепей в распаечных коробках \r\n- установка и подключение накладных розеток и выключателей, накладной розетки для электроплиты, утопленной розетки для электроплиты, звонка, к