INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (61, 'ИП Ворона В.С. ', '', 'logos/415_4_1359306297_slider-img2.jpg', 1, '\r\nВыполняем все виды электромонтажных работ любой степени сложности в производственных, складских, административных, торговых и жилых зданиях, а также объектах индивидуального строительства. \r\n\r\nЭлектромонтаж любой сложности в нашем исполнении - это сочетание выгодных условий с гарантированно высоким качеством работ, обусловленным знанием и опытом. \r\n\r\nПреимущества нашей компании - подбор правильного решения проведения ремонтных работ, подходящих именно Вашему индивидуальному запросу. Мы учтем все уточнения и пожелания в каждом отдельном случае. На все выполненные виды работ мы предоставляет гарантию. \r\n\r\nБлагодаря высокой квалификации, профессионализму и добросовестности наших специалистов, хорошо организованной командной работе, контролю качества на всех этапах работ, гибкой ценовой политике, а также внимательному отношению к потребностям клиентов мы приобрели репутацию выгодного, надежного и безупречного партнера. \r\n\r\nМы всегда рады новым клиентам и готовы обеспечить качественное и своевременное выполнение строительных работ любого объема и любой степени сложности! Будем рады предложить Вам наши услуги и видеть Вас в качестве наших заказчиков и партнеров. \r\n\r\nПоможем приобрести и доставить по выгодным ценам необходимые для заказа материалы.\r\n\r\nВыезд на замер и оценку работы бесплатно \r\nДоговор на выполнение работ \r\nДоставка материалов \r\nВозможна оплата по б/налу ', 'VoroNst1@mail.ru', '', '', '+(375)296658265', 'http://remontvdome.by/', '+(375)295759075', NULL, NULL, NULL);
