INSERT INTO `remont_organizationprofile` (`id`, `name`, `address`, `logo`, `city_id`, `description`, `email`, `fax`, `landline_phone`, `mobile_phone`, `web_site`, `mobile_phone2`, `password`, `login`, `account_id`) VALUES (79, 'Unipron  ', 'г.Минск, Проспект Газеты Правда, 11"Г",', 'logos/317_4_1358241461_mat1.jpg', 1, 'Электрообогрев: электрический теплый пол UNIPRON\r\n\r\nПроизводитель полимерного греющего кабеля UNIPRON, нагревательных матов UNIPRON, нагревательных плит предлагает: \r\n- проектирование, изготовление и монтаж антиобледенительных систем, систем обогрева ферм (нагревательные плиты), трубопроводов, тротуарных и подъездных дорожек, грунта; \r\n- изготавливаем и устанавливаем системы "теплый пол"; \r\n- приглашаем к сотрудничеству частных предпринимателей, плиточников, электриков, монтажников, строительные и проектные организации.\r\n\r\nВыезд на замер и оценку работы бесплатно \r\nДоговор на выполнение работ \r\nДоставка материалов \r\nВозможна оплата по б/налу \r\n', 'vasil@tst.by', '', '+375 17) 207-99-81, 207-49-71', '(+375 44) 530 42 78, 530 42 87', 'http://unipron.by/', '(+375 29) 703 35 04, (+375 33)', '', '', NULL);
